insert into saturn_dashboard_history(
zk_cluster, record_type , topic, content, record_date, create_time
) values

    (
    #{item.zkCluster}, #{item.type}, #{item.topic}, #{item.content}, #{item.recordDate}, #{item.recordDate}
    )

